{"changed":true,"filter":false,"title":"board.rb","tooltip":"/Tic Tac Toe/board.rb","value":"module TicTacToe\n    class Board\n      attr_reader :board\n      def initialize(size)\n          @board = create_empty_board(size)\n          @size = size\n          p 'yo'\n      end\n      \n      def print_board\n          @size.times { print \"-----\" }\n          puts\n          @board.each do |row|\n              row.each do |c|\n                  print \"| #{c} |\"\n              end\n          puts\n          @size.times { print \"-----\" }\n          puts\n          end\n      end\n      \n      def make_move(move, sym)\n        if board[x][y] == ' '\n            board[x][y] = sym\n        else\n            raise ArgumentError, \"Invalid, cell not empty\"\n        end\n      end\n      \n      def each_in_row(row, &block)\n          @board[row].each(&block)\n      end\n      \n      def each_in_col(col, &block)\n          @board.each { |row| block(row[col]) }\n      end\n  \n      def each_cell(&block)\n          @board.each { |row| row.each(&block) }\n      end\n      \n      def [](index)\n          @board[index]\n      end\n  \n      def scoreMove(sym, x, y)\n        @dupboard = @board.dupboard\n        score = relatives(sym, 1, x, y, 1, 0)\n        p score\n        score = relatives(sym, 1, x, y, 0, 1) \n        p score\n        score = relatives(sym, 1, x, y, 1, 1) \n        p score\n        score = relatives(sym, 1, x, y, 1, -1) \n        p score\n      end\n      \n      def relatives(sym, current_score, x, y, dir_x, dir_y)\n        p \"yo #{current_score} at #{x} #{y}\"\n        if valid?(x+dir_x, y+dir_y) && @board[x+dir_x][y+dir_y] == sym\n          current_score += relatives(sym, current_score,\n                                     x+dir_x, y+dir_y, dir_x, dir_y)\n        end\n        if valid?(x-dir_x, y-dir_y) && @board[x-dir_x][y-dir_y] == sym\n          current_score += relatives(sym, current_score,\n                                     x-dir_x, y-dir_y, dir_x, dir_y)\n        end\n        current_score\n      end\n      \n      def valid?(x, y)\n        return true if (x > 0 && x < @size && y > 0 && y < @size)\n        false\n      end\n    private\n      def create_empty_board(size)\n       board = Array.new\n       size.times do\n         row = Array.new\n         size.times do\n           row << ' '\n         end\n         board << row\n        end\n        board\n      end\n    end\nend\n\n\n\nboard = TicTacToe::Board.new(3)\nboard.scoreMove(' ', 0, 0)","undoManager":{"mark":76,"position":100,"stack":[[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":11},"end":{"row":88,"column":12}},"text":"T"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":12},"end":{"row":88,"column":13}},"text":"a"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":13},"end":{"row":88,"column":14}},"text":"c"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":14},"end":{"row":88,"column":15}},"text":"T"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":15},"end":{"row":88,"column":16}},"text":"o"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":16},"end":{"row":88,"column":17}},"text":"e"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":17},"end":{"row":88,"column":18}},"text":":"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":88,"column":18},"end":{"row":88,"column":19}},"text":":"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":68,"column":59},"end":{"row":68,"column":60}},"text":"@"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":57,"column":59},"end":{"row":58,"column":0}},"text":"\r\n"},{"action":"insertText","range":{"start":{"row":58,"column":0},"end":{"row":58,"column":8}},"